import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faQuoteLeft } from '@fortawesome/free-solid-svg-icons';

interface TestimonialProps {
  quote: string;
  author: string;
  company: string;
}

const Testimonial: React.FC<TestimonialProps> = ({ quote, author, company }) => {
  return (
    <div className="bg-gray-50 rounded-lg shadow-md p-6 relative">
      <FontAwesomeIcon
        icon={faQuoteLeft}
        className="text-primary text-3xl absolute top-4 left-4 opacity-50"
      />
      <p className="text-gray-700 italic mt-8 mb-4">"{quote}"</p>
      <div className="flex items-center">
        <div>
          <p className="font-semibold text-gray-800">{author}</p>
          <p className="text-gray-500 text-sm">{company}</p>
        </div>
      </div>
    </div>
  );
};

export default Testimonial;
