// components/TeamMemberCard.tsx
import React from 'react';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { IconProp } from '@fortawesome/fontawesome-svg-core';

interface TeamMemberCardProps {
  name: string;
  role: string;
  bio: string;
  icon: string;
  image: string;
}

const TeamMemberCard: React.FC<TeamMemberCardProps> = ({
  name,
  role,
  bio,
  icon,
  image,
}) => {
  return (
    <motion.div
      className="relative w-64 h-96 cursor-pointer"
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.8 }}
      style={{ perspective: '1000px' }} // Inline perspective for better control
    >
      <motion.div
        className="relative w-full h-full transition-transform duration-500"
        style={{ transformStyle: 'preserve-3d' }}
        whileHover={{ rotateY: -180 }}
      >
        {/* Front Face */}
        <div
          className="absolute w-full h-full bg-white rounded-2xl shadow-lg overflow-hidden"
          style={{ backfaceVisibility: 'hidden' }}
        >
          <div className="relative h-3/4">
            <img
              src={image}
              alt={`${name}'s portrait`}
              className="w-full h-full object-cover object-top"
              onError={(e) => {
                console.error(`Failed to load image: ${image}`);
                e.currentTarget.src = 'https://via.placeholder.com/256x192';
              }}
            />
            <div className="absolute inset-0 bg-gradient-to-b from-transparent to-white/40" />
            <div className="absolute bottom-4 left-4 bg-white/80 px-3 py-1 rounded-full text-sm font-medium text-gray-800 shadow">
              {role}
            </div>
          </div>
          <div className="p-4 text-center">
            <h3 className="text-xl font-semibold text-gray-900">{name}</h3>
          </div>
        </div>

        {/* Back Face */}
        <div
          className="absolute w-full h-full bg-white rounded-2xl shadow-lg overflow-hidden"
          style={{ backfaceVisibility: 'hidden', transform: 'rotateY(180deg)' }}
        >
          <div className="flex flex-col items-center justify-center h-full p-6 text-center">
            <div className="mb-4 bg-blue-500 p-3 rounded-full shadow-lg">
              <FontAwesomeIcon
                icon={icon as IconProp}
                className="text-white text-xl"
              />
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">{name}</h3>
            <p className="text-gray-600 text-sm mb-4">{bio}</p>
            <div className="flex space-x-3">
              {['twitter', 'linkedin', 'github'].map((social) => (
                <motion.a
                  key={social}
                  href="#"
                  whileHover={{ scale: 1.1 }}
                  className="bg-gray-100 hover:bg-blue-500 hover:text-white w-8 h-8 rounded-full flex items-center justify-center text-gray-600 transition-colors"
                >
                  <FontAwesomeIcon
                    icon={`fa-brands fa-${social}` as IconProp}
                    className="text-sm"
                  />
                </motion.a>
              ))}
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default TeamMemberCard;
