// pages/About.tsx
import React from 'react';
import SectionHeader from '../components/SectionHeader';
import text from '../config/text.json';
import { motion } from 'framer-motion';
import TeamCarousel from '../components/TeamCarousel';

const About: React.FC = () => {
  return (
    <div className="bg-gray-50">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-r from-primary to-primary-dark py-28 overflow-hidden">
        {/* Background Image */}
        <div
          className="absolute inset-0 bg-cover bg-center opacity-80"
          style={{ backgroundImage: `url(${text.about.imageUrl})` }}
        />

        {/* Dark Overlay with Blur */}
        <div className="absolute inset-0 bg-black/50 backdrop-blur-sm" />

        {/* Content */}
        <div className="container mx-auto px-4 text-center relative z-10">
          <motion.h1
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-4xl md:text-5xl font-bold text-white mb-6"
          >
            {text.about.heroTitle}
          </motion.h1>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.3 }}
            className="text-xl text-white/90 max-w-3xl mx-auto"
          >
            {text.about.heroSubtitle}
          </motion.p>
        </div>
      </section>

      {/* about Section */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <SectionHeader
            subtitle={text.about.About}
          />
        </div>
      </section>

      {/* Mission Section */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <SectionHeader
            title={text.about.missionTitle}
            subtitle={text.about.missionText}
          />
        </div>
      </section>

      {/* Vision Section */}
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <SectionHeader
            title={text.about.visionTitle}
            subtitle={text.about.visionText}
          />
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            whileInView={{ opacity: 1, scale: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
            className="bg-white p-8 rounded-xl shadow-md max-w-4xl mx-auto"
          >
            <div className="flex items-center">
              <div className="text-primary text-5xl mr-6">"</div>
              <p className="text-lg text-gray-700">
                {text.about.visionText}
              </p>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Team Section */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <SectionHeader
            title={text.about.teamTitle}
            subtitle={text.about.teamSubtitle}
          />
         <TeamCarousel teamMembers={text.about.teamMembers} />
        </div>
      </section>
    </div>
  );
};

export default About;
