import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight, faChartLine, faUsers, faAward } from '@fortawesome/free-solid-svg-icons';
import { motion, useScroll, useTransform } from 'framer-motion';
import Card from '../components/Card';
import Testimonial from '../components/Testimonial';
import text from '../config/text.json';
import TeamAvatars from '../components/TeamAvatars';
import StatsCounter from '../components/StatsCounter';

const Home: React.FC = () => {
  const { scrollYProgress } = useScroll();
  const yPos = useTransform(scrollYProgress, [0, 1], [0, -100]);

  return (
    <div className="bg-white overflow-hidden">
      {/* Hero Section - More sophisticated with parallax effect */}
      <section className="relative h-screen flex items-center justify-center overflow-hidden">
        <motion.div 
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage: `url(${text.hero.imageUrl})`,
            y: yPos,
            scale: 1.1
          }}
          initial={{ scale: 1.2 }}
          animate={{ scale: 1 }}
          transition={{ duration: 1.5 }}
        />
        <div className="absolute inset-0 bg-gradient-to-b from-black/80 via-black/50 to-black/30"></div>

        <div className="container mx-auto px-6 text-center relative z-10">
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl lg:text-7xl font-bold text-white mb-6 leading-tight">
              {text.home.heroTitle}
            </h1>
            <p className="text-xl md:text-2xl text-gray-200 mb-10 opacity-90">
              {text.home.heroSubtitle}
            </p>
            <div className="flex flex-col sm:flex-row justify-center gap-4">
              <Link
                to="/contact"
                className="inline-flex items-center justify-center bg-blue-600 hover:bg-blue-700 text-white py-4 px-8 rounded-lg text-lg font-medium transition-all duration-300 shadow-lg hover:shadow-xl min-w-[200px]"
              >
                {text.home.heroButton}
                <FontAwesomeIcon icon={faArrowRight} className="ml-3" />
              </Link>
              <Link
                to="/services"
                className="inline-flex items-center justify-center bg-transparent border-2 border-white text-white py-4 px-8 rounded-lg text-lg font-medium transition-all duration-300 hover:bg-white/10 min-w-[200px]"
              >
                Explore Services
              </Link>
            </div>
          </motion.div>
        </div>

        <motion.div 
          className="absolute bottom-10 left-1/2 transform -translate-x-1/2"
          animate={{ y: [0, 10, 0] }}
          transition={{ repeat: Infinity, duration: 2 }}
        >
          <div className="text-white text-sm animate-bounce">Scroll to explore</div>
        </motion.div>
      </section>

      {/* Trusted By Section */}
      <section className="py-12 bg-gray-50 border-b border-gray-200">
        <div className="container mx-auto px-6">
          <p className="text-center text-gray-500 text-sm uppercase tracking-wider mb-6">Trusted by industry leaders</p>
          <div className="flex flex-wrap justify-center items-center gap-8 md:gap-16 opacity-70">
            {[1, 2, 3, 4, 5].map((i) => (
              <img 
                key={i} 
                src={`/logos/company-${i}.svg`} 
                alt="Company logo" 
                className="h-8 md:h-10 grayscale hover:grayscale-0 transition-all duration-300"
              />
            ))}
          </div>
        </div>
      </section>

      {/* Impact Section - More refined with animated counters */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-6">
          <div className="text-center mb-16">
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="text-blue-600 font-semibold mb-4"
            >
              Our Global Impact
            </motion.p>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
              className="text-3xl md:text-4xl font-bold text-gray-900 mb-6 max-w-2xl mx-auto"
            >
              Delivering measurable results for our clients
            </motion.h2>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="bg-gray-50 p-8 rounded-xl border border-gray-100 hover:border-blue-100 transition-all duration-300"
            >
              <div className="text-blue-600 mb-4">
                <FontAwesomeIcon icon={faChartLine} size="2x" />
              </div>
              <StatsCounter 
                value={500} 
                suffix="+" 
                className="text-4xl font-bold text-gray-900 mb-2" 
              />
              <h3 className="text-lg font-semibold text-gray-800 mb-2">Projects Delivered</h3>
              <p className="text-gray-600">Across 15+ industries worldwide</p>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
              className="bg-gray-50 p-8 rounded-xl border border-gray-100 hover:border-blue-100 transition-all duration-300"
            >
              <div className="text-blue-600 mb-4">
                <FontAwesomeIcon icon={faUsers} size="2x" />
              </div>
              <StatsCounter 
                value={200} 
                suffix="+" 
                className="text-4xl font-bold text-gray-900 mb-2" 
              />
              <h3 className="text-lg font-semibold text-gray-800 mb-2">Satisfied Clients</h3>
              <p className="text-gray-600">With 95% retention rate</p>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
              className="bg-gray-50 p-8 rounded-xl border border-gray-100 hover:border-blue-100 transition-all duration-300"
            >
              <div className="text-blue-600 mb-4">
                <FontAwesomeIcon icon={faAward} size="2x" />
              </div>
              <StatsCounter 
                value={10} 
                suffix="+" 
                className="text-4xl font-bold text-gray-900 mb-2" 
              />
              <h3 className="text-lg font-semibold text-gray-800 mb-2">Years of Excellence</h3>
              <p className="text-gray-600">Industry-recognized expertise</p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Services Section - More professional card layout */}
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-6">
          <div className="text-center mb-16">
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="text-blue-600 font-semibold mb-4"
            >
              Our Services
            </motion.p>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
              className="text-3xl md:text-4xl font-bold text-gray-900 mb-4"
            >
              {text.home.servicesTitle}
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
              className="text-lg text-gray-600 max-w-2xl mx-auto"
            >
              {text.home.servicesSubtitle}
            </motion.p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
            {text.home.services.map((service, index) => (
              <motion.div
                key={service.title}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                whileHover={{ y: -5 }}
                transition={{ 
                  duration: 0.5, 
                  delay: index * 0.1,
                  hover: { duration: 0.3 } 
                }}
                viewport={{ once: true, margin: "-100px" }}
                className="group"
              >
                <Card
                  title={service.title}
                  description={service.description}
                  icon={service.icon}
                  className="h-full bg-white shadow-sm group-hover:shadow-md transition-all duration-300 rounded-lg border border-gray-100 overflow-hidden"
                  headerClassName="bg-gradient-to-r from-blue-600 to-blue-500 text-white p-6"
                  bodyClassName="p-6"
                >
                  <Link 
                    to={`/services#${service.title.toLowerCase().replace(/\s+/g, '-')}`}
                    className="mt-4 inline-flex items-center text-blue-600 hover:text-blue-700 font-medium"
                  >
                    Learn more
                    <FontAwesomeIcon icon={faArrowRight} className="ml-2" />
                  </Link>
                </Card>
              </motion.div>
            ))}
          </div>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            viewport={{ once: true }}
            className="text-center mt-12"
          >
            <Link
              to="/services"
              className="inline-flex items-center text-blue-600 hover:text-blue-700 font-semibold text-lg"
            >
              View all services
              <FontAwesomeIcon icon={faArrowRight} className="ml-2" />
            </Link>
          </motion.div>
        </div>
      </section>

      {/* Testimonials Section - More elegant layout */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-6">
          <div className="text-center mb-16">
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="text-blue-600 font-semibold mb-4"
            >
              Client Success Stories
            </motion.p>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
              className="text-3xl md:text-4xl font-bold text-gray-900 mb-4"
            >
              {text.home.testimonialsTitle}
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
              className="text-lg text-gray-600 max-w-2xl mx-auto"
            >
              {text.home.testimonialsSubtitle}
            </motion.p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
            <div className="lg:col-span-2">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {text.home.testimonials.slice(0, 2).map((testimonial, index) => (
                  <motion.div
                    key={testimonial.author}
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6, delay: index * 0.2 }}
                    viewport={{ once: true }}
                  >
                    <Testimonial
                      quote={testimonial.quote}
                      author={testimonial.author}
                      company={testimonial.company}
                      className="bg-gray-50 border border-gray-100 rounded-xl p-6 h-full"
                      avatarSize="lg"
                    />
                  </motion.div>
                ))}
              </div>
            </div>

            <div className="flex flex-col gap-8">
              {text.home.testimonials.slice(2).map((testimonial, index) => (
                <motion.div
                  key={testimonial.author}
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
                  viewport={{ once: true }}
                >
                  <Testimonial
                    quote={testimonial.quote}
                    author={testimonial.author}
                    company={testimonial.company}
                    className="bg-gray-50 border border-gray-100 rounded-xl p-6 h-full"
                  />
                </motion.div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Team Section - More professional presentation */}
      <section className="py-20 bg-gray-900 text-white">
        <div className="container mx-auto px-6">
          <div className="text-center mb-16">
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="text-blue-400 font-semibold mb-4"
            >
              Our Experts
            </motion.p>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
              className="text-3xl md:text-4xl font-bold mb-4"
            >
              Meet Our Leadership Team
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
              className="text-lg text-gray-300 max-w-2xl mx-auto"
            >
              Seasoned professionals with diverse expertise driving your success
            </motion.p>
          </div>

          <TeamAvatars 
            teamMembers={text.about.teamMembers} 
            className="max-w-5xl mx-auto"
            avatarSize="xl"
            showDetails
          />

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            viewport={{ once: true }}
            className="text-center mt-12"
          >
            <Link
              to="/about"
              className="inline-flex items-center text-white hover:text-blue-300 font-semibold text-lg border-b border-transparent hover:border-blue-300 pb-1"
            >
              Learn more about our team
              <FontAwesomeIcon icon={faArrowRight} className="ml-2" />
            </Link>
          </motion.div>
        </div>
      </section>

      {/* CTA Section - More compelling with gradient background */}
      <section className="py-20 bg-gradient-to-r from-blue-600 to-blue-800">
        <div className="container mx-auto px-6 text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="max-w-3xl mx-auto"
          >
            <h2 className="text-3xl md:text-4xl font-bold text-white mb-6">
              Ready to Elevate Your Business?
            </h2>
            <p className="text-xl text-blue-100 mb-8">
              Schedule a consultation with our experts to discuss how we can help you achieve your goals.
            </p>
            <div className="flex flex-col sm:flex-row justify-center gap-4">
              <Link
                to="/contact"
                className="inline-flex items-center justify-center bg-white text-blue-600 hover:bg-gray-100 py-4 px-8 rounded-lg text-lg font-semibold transition-all duration-300 shadow-lg hover:shadow-xl min-w-[200px]"
              >
                Get Started
                <FontAwesomeIcon icon={faArrowRight} className="ml-3" />
              </Link>
              <Link
                to="/case-studies"
                className="inline-flex items-center justify-center bg-transparent border-2 border-white text-white hover:bg-white/10 py-4 px-8 rounded-lg text-lg font-semibold transition-all duration-300 min-w-[200px]"
              >
                View Case Studies
              </Link>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
