// components/TeamAvatars.tsx
import React from 'react';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { IconProp } from '@fortawesome/fontawesome-svg-core';
import { library } from '@fortawesome/fontawesome-svg-core';
import { fas } from '@fortawesome/free-solid-svg-icons';

// Add all solid icons to the library
library.add(fas);

interface TeamMember {
  name: string;
  role: string;
  bio: string;
  icon: string; // This should be the name of a FontAwesome icon
  image: string;
}

interface TeamAvatarsProps {
  teamMembers: TeamMember[];
}

const TeamAvatars: React.FC<TeamAvatarsProps> = ({ teamMembers }) => {
  // Function to convert string icon name to IconProp
  const getIconProp = (iconName: string): IconProp => {
    // Assuming all icons are from the 'fas' collection
    return ['fas', iconName] as IconProp;
  };

  return (
    <section className="py-20 bg-white">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-12"
        >
          <h2 className="text-4xl font-bold text-gray-900 mb-4">Meet Our Team</h2>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Hover over our team members to learn more about the talented individuals driving our success.
          </p>
        </motion.div>

        <div className="flex flex-wrap justify-center gap-8">
          {teamMembers.map((member, index) => (
            <div key={member.name} className="relative">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ scale: 1.1, zIndex: 10 }}
                className="relative flex-none w-20 h-20 md:w-24 md:h-24"
              >
                {/* Avatar Image */}
                <img
                  src={member.image}
                  alt={`${member.name}'s avatar`}
                  className="w-full h-full rounded-full object-cover border-2 border-gray-200 shadow-md group-hover:border-blue-500 transition-colors"
                  onError={(e) => {
                    console.error(`Failed to load image for ${member.name}: ${member.image}`);
                    e.currentTarget.src = 'https://via.placeholder.com/96?text=' + encodeURIComponent(member.name.charAt(0));
                  }}
                />
              </motion.div>

              {/* Tooltip with member details */}
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                whileHover={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.2 }}
                className="absolute top-full left-1/2 transform -translate-x-1/2 mt-2 bg-white rounded-lg shadow-xl overflow-hidden z-20 pointer-events-none"
              >
                <div className="p-4 text-center min-w-[220px]">
                  <h3 className="text-lg font-semibold text-gray-900">{member.name}</h3>
                  <p className="text-sm text-gray-600 mb-2">{member.role}</p>
                  <div className="mt-2 flex justify-center">
                    <div className="bg-blue-100 p-2 rounded-full">
                      <FontAwesomeIcon
                        icon={getIconProp(member.icon)}
                        className="text-blue-600 text-sm"
                      />
                    </div>
                  </div>
                  <p className="text-xs text-gray-500 mt-2 line-clamp-3">{member.bio}</p>
                </div>
              </motion.div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TeamAvatars;
